import * as ImagePicker from 'expo-image-picker';
import React, { useState, useEffect } from 'react';
import { StyleSheet,Pressable,TouchableOpacity,Button,Dimensions, Image, View, Text,Alert  } from 'react-native';
import { BarCodeScanner } from 'expo-barcode-scanner';

import { Ionicons } from '@expo/vector-icons'; 

const {height:SCREEN_HEIGHT, width:SCREEN_WIDTH} = Dimensions.get('window');

function ImagePickerComponent({ onSubmit }) {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('Please add an image');
  const [scanned, setScanned] = useState(false);

  const handleBarCodeScanned = ({ type, data }) => {
    alert(`Bar code with type ${type} and data ${data} has been scanned!`);
  };

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      base64: true, //return base64 data.
      //this will allow the Vision API to read this image.
    });
    if (!result.cancelled) { //if the user submits an image,
      const responseData = await onSubmit(result.base64).catch((error)=>{
        console.log("Api call error");
        //alert(error.message);
     });
    }

  };
  return (
    <View>
        <Button 
                    style={[styles.rightView,styles.browseBtn]}
                    onPress={()=>pickImage }>
                      <Text style={[{
                      color: 'black',
                      fontWeight: 'bold',
                      textAlign: 'center',
                    },{
                      borderRadius: 20,
                      padding: 10,
                      elevation: 2,
                    }]}>갤러리에서 불러오기</Text>
                  </Button>
    </View>
  );
}
export default ImagePickerComponent;

const styles = StyleSheet.create({
    main: {
      flex:1,
      backgroundColor: 'white',
    },
    rightView : {
      flex: 1,
      justifyContent: 'flex-end',
      alignItems : 'right',
    },
    centeredView: {
      flex: 1,
      justifyContent: 'flex-end',
      alignItems: 'center',
    },
    button: {
      borderRadius: 20,
      padding: 10,
      elevation: 2,
    },
    buttonOpen: {
      backgroundColor: '#F194FF',
    },
    buttonClose: {
      backgroundColor: '#2196F3',
    },
    textStyle: {
      color: 'white',
      fontWeight: 'bold',
      textAlign: 'center',
    },
    browseBtn : {
      borderRadius: 20,
      padding: 10,
      elevation: 2,
      backgroundColor: 'black',
    }
  });
